    openDialogConfirm(mensaje, array) {
      this.banderaConfirmar = false;
      this.textoConfirmar = mensaje;
      let pos;
      pos = {
        top: '34vh'
      };
      this.dialogConfirmar = this.dialog.open(this.confirmDialog, {
        width: '30%',
        disableClose: true,
        position: pos
      });
      this.dialogConfirmar.afterClosed().subscribe(confirmado => {
        if (this.banderaConfirmar) {
          switch (this.textoConfirmar) {
            case 'Está seguro de reiniciar la configuración':
              this.reiniciar();
              break;
            case '¿Está seguro de guradar los cambios ralizados?':
              this.ajustarTiempos();
              break;
            case 'Está seguro de finalizar el cronómetro. Todos los valores se reiniciarán':
              this.finalizarCronometro();
              break;
            case '¿Está seguro que desea pasar al siguiente bloque?':
              this.nuevoBloque(array);
              break;
          }
        }
      });
    }
    selectLogo(id, clase) {
      if (this.verificarLogos(id)) {
        this.arrayLogos.push(id);
        this.candidatosArray[this.indexSeleccionado].id_logo = id;
        this.candidatosArray[this.indexSeleccionado].id_clase = clase;
        this.dialogRef.close();
      } else this.openDialogConfirm('Este logo ya está siendo usado por otro candidato');
    }
    verificarLogos(id) {
      let bandera = true;
      if (this.arrayLogos.length != 0) {
        for (const iterator of this.arrayLogos) {
          if (iterator == id) bandera = false;
        }
      }
      return bandera;
    }
    quitarLogo(index, id, bandera) {
      if (id != 0) {
        this.candidatosArray[index].id_logo = 0;
        this.candidatosArray[index].id_clase = 0;
        let indexLogos = 0;
        let bandera_logos = false;
        for (const iterator of this.arrayLogos) {
          if (iterator == id) {
            bandera_logos = true;
            break;
          }
          indexLogos++;
        }
        if (bandera_logos) this.arrayLogos.splice(indexLogos, 1);
      }
    }
    reiniciar() {
      // if (confirm('Está seguro de reiniciar la configuración')) {
      this.tipoTiempo = 'Configuración Inicial';
      this.candidatosArray = new Array();
      this.arrayLogos = [];
      this.confInicial = new ConfInicial();
      this.estado = new Array();
      //banderadiscusion
      this.banderaPreparados = false;
      this.banderaDiscusion = false;
      this.banderaResumen = false;
      this.banderaExpo = false;
      this.banderaTema = false;
      this.banderaTransmitir = false;
      this.banderaCierre = true;
      this.numeroBloque = 1;
      this.iniciarSeleccionado();
      localStorage.clear();
      // } else {
      //   console.log('canceló');
      // }
    }
    guardarCandidatos(valid, editar) {
      if (valid) {
        if (this.candidatosArray.length == this.arrayLogos.length) {
          this.tipoTiempo = 'Listo Para Comenzar';
          this.banderaPreparados = true;
          this.almacenamientoLocal.setArrayCandidatos(this.candidatosArray);
          this.almacenamientoLocal.setBanderaPreparadados(true);
          this.almacenamientoLocal.setTipoTiempo(this.tipoTiempo);
          this.banderaEditarCandidatos = false;
        } else {
          if (editar === true) {
            this.almacenamientoLocal.setArrayCandidatos(this.candidatosArray);
            this.banderaEditarCandidatos = false;
          } else this.openDialogConfirm('Verifique que todos los candidatos tengan un logo');
        }
      } else {
        this.openDialogConfirm('Debe de añadir el nombre del candidato a todos los campos');
      }
    }
    iniciarTiempo(index, tipo_tiempo) {
      this.banderaTransmitir = true;
      this, this.banderaExpo = true;
      this.tipoTiempo = tipo_tiempo;
      this.claveTiempo = this.tipoTiempo == 'EXPONIENDO' ? 'tiempo_exposicion' :
      // : this.tipoTiempo == 'SEGUIMIENTO MODERACIÓN'
      // ? 'tiempo_seg_bolsa'
      this.tipoTiempo == 'TEMA' ? 'tiempo_tema' : this.tipoTiempo == 'DISCUSIÓN' ? 'tiempo_dis' : this.tipoTiempo == 'PREGUNTA' ? 'tiempo_segmento1' : 'tiempo_segmento2';
      this.detenerTiempo();
      this.indexCandidatoArray = index;
      this.candidatoSeleccionado.id_clase = this.candidatosArray[index].id_clase;
      this.candidatoSeleccionado.id_logo = this.candidatosArray[index].id_logo;
      this.candidatoSeleccionado.nombre = this.candidatosArray[index].nombre;
      if (this.banderaResumen) {
        this.candidatoSeleccionado.tiempo.min = this.candidatosArray[index][this.claveTiempo].min;
        this.candidatoSeleccionado.tiempo.seg = this.candidatosArray[index][this.claveTiempo].seg;
        this.candidatoSeleccionado.tiempo.terminado = this.candidatosArray[index][this.claveTiempo].terminado;
      }
      if (this.banderaTema) {
        this.candidatoSeleccionado.tiempo.min = this.candidatosArray[index][this.claveTiempo].min;
        this.candidatoSeleccionado.tiempo.seg = this.candidatosArray[index][this.claveTiempo].seg;
        this.candidatoSeleccionado.tiempo.terminado = this.candidatosArray[index][this.claveTiempo].terminado;
      } else {
        this.calcularTiempoBolsa(index);
      }
      this.inicarTemporizador();
    }
    //
    calcularTiempoBolsa(index) {
      if (this.confInicial.tiempo_bolsa_min == this.candidatosArray[index][this.claveTiempo].min) {
        if (this.candidatosArray[index][this.claveTiempo].seg > this.confInicial.tiempo_bolsa_seg) {
          this.candidatoSeleccionado.tiempo.min = this.confInicial.tiempo_bolsa_min;
          this.candidatoSeleccionado.tiempo.seg = this.confInicial.tiempo_bolsa_seg;
        } else {
          this.candidatoSeleccionado.tiempo.min = this.candidatosArray[index][this.claveTiempo].min;
          this.candidatoSeleccionado.tiempo.seg = this.candidatosArray[index][this.claveTiempo].seg;
          this.candidatoSeleccionado.tiempo.terminado = this.candidatosArray[index][this.claveTiempo].terminado;
        }
      } else if (this.candidatosArray[index][this.claveTiempo].min > this.confInicial.tiempo_bolsa_min) {
        this.candidatoSeleccionado.tiempo.min = this.confInicial.tiempo_bolsa_min;
        this.candidatoSeleccionado.tiempo.seg = this.confInicial.tiempo_bolsa_seg;
      } else {
        this.candidatoSeleccionado.tiempo.min = this.candidatosArray[index][this.claveTiempo].min;
        this.candidatoSeleccionado.tiempo.seg = this.candidatosArray[index][this.claveTiempo].seg;
        this.candidatoSeleccionado.tiempo.terminado = this.candidatosArray[index][this.claveTiempo].terminado;
      }
    }
    //Debate
    cierreDebate() {
      let min_cierre = document.getElementById('inp_min_cierre');
      let seg_cierre = document.getElementById('inp_seg_cierre');
      let bandera = this.validarTiempoCierre(min_cierre, seg_cierre);
      this.bandera_guardar = !bandera;
      if (bandera) {
        let array = [];
        for (let i = 0; i < this.candidatosArray.length; i++) {
          let candidato = {
            nombre: this.banderaPreparados ? this.candidatosArray[i].nombre : '',
            id_logo: this.banderaPreparados ? this.candidatosArray[i].id_logo : 0,
            id_clase: this.banderaPreparados ? this.candidatosArray[i].id_clase : 0,
            tiempo_exposicion: {
              min: Number(this.formatTwoDigits(min_cierre.value)),
              seg: Number(this.formatTwoDigits(seg_cierre.value)),
              terminado: false
            },
            tiempo_segmento1: {
              min: 0,
              seg: 0,
              terminado: false
            },
            tiempo_segmento2: {
              min: 0,
              seg: 0,
              terminado: false
            },
            tiempo_tema: {
              min: 0,
              seg: 0,
              terminado: false
            },
            tiempo_dis: {
              min: 0,
              seg: 0,
              terminado: false
            }
          };
          array.push(candidato);
        }
        var d = new Date();
        this.estado.push({
          bloque: this.numeroBloque,
          fecha: `${this.formatTwoDigits(d.getDate())} de ${this.months[d.getMonth()]} del ${d.getFullYear()}`,
          hora: `${this.formatTwoDigits(d.getHours())}:${this.formatTwoDigits(d.getMinutes())}`,
          estado: this.candidatosArray
        });
        this.numeroBloque++;
        this.almacenamientoLocal.setNumeroBloque(this.numeroBloque);
        this.almacenamientoLocal.setArrayEstado(this.estado);
        this.almacenamientoLocal.setBanderaCierre(true);
        //
        this.candidatosArray = array;
        this.almacenamientoLocal.setArrayCandidatos(this.candidatosArray);
        this.dialogCerrar.close();
        this.banderaResumen = true;
        this.banderaExpo = true;
        this.banderaTema = true;
        this.banderaCierre = true;
      }
    }
    ///Tiempo
    inicarTemporizador() {
      if (!this.banderaResumen) this.candidatoSeleccionado.tiempo.terminado = false;
      if (!this.banderaTema) this.candidatoSeleccionado.tiempo.terminado = false;
      if (!this.banderaExpo) this.candidatoSeleccionado.tiempo.terminado = false;
      this.detenerTiempo();
      document.getElementById(this.claveTiempo + this.indexCandidatoArray).classList.add('blink_me');
      let boton = document.getElementById(this.claveTiempo + '_button' + this.indexCandidatoArray);
      // .classList.add('btn-danger');
      boton.classList.add('btn-danger');
      boton.classList.add('blink_me');
      boton.innerHTML = 'Ocupado';
      // this.guardarRegistros();
      this.tempo = setInterval(() => this.temporizar(), 1000);
    }
    detenerTiempo() {
      // let conf = this.almacenamientoLocal.getConfInicial();
      // this.confInicial.tiempo_bolsa_min = conf.tiempo_bolsa_min;
      // this.confInicial.tiempo_bolsa_seg = conf.tiempo_bolsa_seg;
      clearInterval(this.tempo);
      if (this.indexCandidatoArray != undefined) {
        let botones = ['tiempo_exposicion',
        // 'tiempo_seg_bolsa',
        'tiempo_segmento1', 'tiempo_segmento2', 'tiempo_tema', 'tiempo_dis'];
        botones.forEach(element => {
          try {
            document.getElementById(element + this.indexCandidatoArray).classList.remove('blink_me');
            let boton = document.getElementById(element + '_button' + this.indexCandidatoArray);
            // .classList.add('btn-danger');
            boton.classList.remove('btn-danger');
            boton.classList.remove('blink_me');
            boton.innerHTML = 'Iniciar';
          } catch (error) {}
        });
      }
    }
    temporizar() {
      //Bandera para saber si es expo
      if (this.banderaResumen && this.banderaTema) {
        this.banderaExpo = false;
      }
      if (this.tipoTiempo == 'DISCUSIÓN') {
        console.log('Este es un mensaje de ejemplo');
        this.banderaDiscusion = true;
      }
      //que entre a resumen
      if (this.banderaResumen && this.banderaExpo) {
        this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg--;
        if (--this.candidatoSeleccionado.tiempo.seg < 0) {
          this.candidatoSeleccionado.tiempo.seg = 59;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 59;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min--;
          if (--this.candidatoSeleccionado.tiempo.min < 0) {
            this.candidatoSeleccionado.tiempo.seg = 0;
            this.candidatoSeleccionado.tiempo.min = 0;
            this.candidatoSeleccionado.tiempo.terminado = true;
            //
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 0;
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min = 0;
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].terminado = true;
            this.detenerTiempo();
            this.dejarTransmitir();
          }
        }
      }
      //bandera tema prueba
      if (this.banderaTema) {
        this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg--;
        if (--this.candidatoSeleccionado.tiempo.seg < 0) {
          this.candidatoSeleccionado.tiempo.seg = 59;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 59;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min--;
          if (--this.candidatoSeleccionado.tiempo.min < 0) {
            this.candidatoSeleccionado.tiempo.seg = 0;
            this.candidatoSeleccionado.tiempo.min = 0;
            this.candidatoSeleccionado.tiempo.terminado = true;
            //
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 0;
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min = 0;
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].terminado = true;
            this.detenerTiempo();
            this.dejarTransmitir();
          }
        }
      }
      //else {
      // if (this.descontarBolsa()) this.descontarSegmentoDiscusion();
      // }
      this.almacenamientoLocal.setArrayCandidatos(this.candidatosArray);
    }
    descontarSegmentoDiscusion() {
      if (--this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg < 0) {
        this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 59;
        if (--this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min < 0) {
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg = 0;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min = 0;
          this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].terminado = true;
        }
      }
    }
    descontarBolsa() {
      if (--this.candidatoSeleccionado.tiempo.seg < 0) {
        this.candidatoSeleccionado.tiempo.seg = 59;
        if (--this.candidatoSeleccionado.tiempo.min < 0) {
          this.candidatoSeleccionado.tiempo.seg = 0;
          this.candidatoSeleccionado.tiempo.min = 0;
          this.candidatoSeleccionado.tiempo.terminado = true;
          //
          if (this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].seg == 0 && this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].min == 0) {
            this.candidatosArray[this.indexCandidatoArray][this.claveTiempo].terminado = true;
          }
          //
          this.detenerTiempo();
          this.dejarTransmitir();
          return false;
        }
      }
      return true;
    }
    dejarTransmitir() {
      setTimeout(() => {
        this.banderaTransmitir = false;
      }, 2000);
    }
    ajustarTiempos() {
      // if (confirm('¿Está seguro de guradar los cambios ralizados?')) {
      this.banderaAjusteTiempo = true;
      this.ajusteArray = [];
      for (let index = 0; index < this.candidatosArray.length; index++) {
        let valores = {};
        //Exposición
        let expMin = document.getElementById('min_exposicion' + index);
        valores['expMin'] = expMin.value;
        let expSeg = document.getElementById('seg_exposicion' + index);
        valores['expSeg'] = expSeg.value;
        // Segmento 1
        let seg1Min = document.getElementById('min_seg1' + index);
        valores['seg1Min'] = seg1Min.value;
        let seg1Seg = document.getElementById('seg_seg1' + index);
        valores['seg1Seg'] = seg1Seg.value;
        // Segmento 2
        let seg2Min = document.getElementById('min_seg2' + index);
        valores['seg2Min'] = seg2Min.value;
        let seg2Seg = document.getElementById('seg_seg2' + index);
        valores['seg2Seg'] = seg2Seg.value;
        this.ajusteArray.push(valores);
        //Tema
        let temaMin = document.getElementById('min_tema' + index);
        valores['temaMin'] = temaMin.value;
        let temaSeg = document.getElementById('seg_tema' + index);
        valores['temaSeg'] = temaSeg.value;
        this.ajusteArray.push(valores);
        //Discusion
        let disMin = document.getElementById('min_dis' + index);
        valores['disMin'] = disMin.value;
        let disSeg = document.getElementById('seg_dis' + index);
        valores['disSeg'] = disSeg.value;
        this.ajusteArray.push(valores);
      }
      this.banderaAjusteTiempo = !this.validarAjusteTiempo();
      if (!this.banderaAjusteTiempo) {
        this.reajustarTiempos();
        this.banderaAjusteTiempo = false;
        this.dialogRefTiempo.close();
      }
      // }
    }
    reajustarTiempos() {
      for (let index = 0; index < this.ajusteArray.length; index++) {
        // Exposición
        if (this.ajusteArray[index].expMin != '') {
          this.candidatosArray[index].tiempo_exposicion.min = Number(this.formatTwoDigits(this.ajusteArray[index].expMin));
        }
        if (this.ajusteArray[index].expSeg != '') {
          this.candidatosArray[index].tiempo_exposicion.seg = Number(this.formatTwoDigits(this.ajusteArray[index].expSeg));
        }
        // Segmento 1
        if (this.ajusteArray[index].seg1Min != '') {
          this.candidatosArray[index].tiempo_segmento1.min = Number(this.formatTwoDigits(this.ajusteArray[index].seg1Min));
        }
        if (this.ajusteArray[index].seg1Seg != '') {
          this.candidatosArray[index].tiempo_segmento1.seg = Number(this.formatTwoDigits(this.ajusteArray[index].seg1Seg));
        }
        // Segemento 2
        if (this.ajusteArray[index].seg2Min != '') {
          this.candidatosArray[index].tiempo_segmento2.min = Number(this.formatTwoDigits(this.ajusteArray[index].seg2Min));
